import React from 'react';

const Album = ({ album, handlePlayTrack, currentTrackIndex, currentAlbumIndex }) => {
  const { coverUrl, title, artist, tracks, albumId } = album;

  return (
    <div className="album row">
      <div className="col">
        <img src={coverUrl} alt={title} />
      </div>
      <div className="col">
        <h2 className="Album Title">{title}</h2>
        <h3 className="Artist Name">{artist}</h3>
      </div>

      <ul className="row">
        {tracks.map((track, index, id) => (
          <li
            key={index}
            className={`row trackList ${currentTrackIndex === index ? 'playing' : ''}`}
            onClick={() => handlePlayTrack(index, id)}
          >
            <span>{track.title} {track.key}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Album;
