import React, { useState, useRef } from 'react';
import Album from './Album';
import data from './data.json';
import MyNavbar from './MyNavbar';
import ReactAudioPlayer from 'react-audio-player';

const Albums = () => {
  const [currentTrackIndex, setCurrentTrackIndex] = useState(null);
  const audioPlayerRef = useRef(null);

  const handlePlayTrack = (index) => {
    setCurrentTrackIndex(index);
  };

  return (
    <div>
      <MyNavbar />
      <div className="albums">
        {data.map((album) => (
          <Album key={album.id} album={album} handlePlayTrack={handlePlayTrack} currentTrackIndex={currentTrackIndex} />
        ))}
      </div>

      <div className="playerArea col">
        <ReactAudioPlayer
          className=""
          src={currentTrackIndex !== null ? data[currentTrackIndex].tracks[currentTrackIndex].audioUrl : ''}
          controls
          ref={audioPlayerRef}
          autoPlay
        />
      </div>

    </div>

  );
};

export default Albums;
