import React, { useState } from 'react';
import Track from './Track';
import AudioPlayer from 'react-h5-audio-player';
// import 'react-h5-audio-player/lib/styles.css';

const Album = ({ album, currentTrackIndex, handlePlayTrack, handlePause, handleEnded }) => {
  const { coverUrl, title, artist, tracks } = album;

  const handleTrackClick = (index) => {
    if (index !== currentTrackIndex) {
      handlePause();
      handlePlayTrack(index);
    }
  };

  return (
    <div className="album row">
      <h2>{title}</h2>
      <h4>{artist}</h4>
      <img src={coverUrl} alt={title} />

      <ul className="row">
        {tracks.map((track, index) => (
          <li key={index} className="row" onClick={() => handleTrackClick(index)}>
            <p>{track.title}</p>
          </li>
        ))}
      </ul>

      <button onClick={() => handlePlayTrack(0)}>Play All</button>
      <button onClick={handlePause}>Pause</button>
    </div>
  );
};

const Player = ({ album, currentTrackIndex, isPlaying, handlePlayTrack, handlePause, handleEnded }) => {
  const [audio] = useState(new Audio());

  const handlePlay = (index) => {
    audio.src = album.tracks[index].audioUrl;
    audio.play();
  };

  const handlePauseClick = () => {
    audio.pause();
  };

  const handleEndedClick = () => {
    handleEnded();
  };

  if (currentTrackIndex !== null) {
    if (isPlaying) {
      if (audio.paused) {
        handlePlay(currentTrackIndex);
      }
    } else {
      handlePauseClick();
    }

    audio.removeEventListener('ended', handleEndedClick);
    audio.addEventListener('ended', handleEndedClick);
  } else {
    handlePauseClick();
    audio.removeEventListener('ended', handleEndedClick);
  }

  return (
    <AudioPlayer
      src={album.tracks[currentTrackIndex]?.audioUrl}
      onPlay={() => handlePlay(currentTrackIndex)}
      onPause={handlePauseClick}
      onEnded={handleEndedClick}
    />
  );
};

const AlbumPlayer = ({ album }) => {
  const [currentTrackIndex, setCurrentTrackIndex] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  const handlePlayTrack = (index) => {
    setCurrentTrackIndex(index);
    setIsPlaying(true);
  };

  const handlePause = () => {
    setIsPlaying(false);
  };

  const handleEnded = () => {
    if (currentTrackIndex < album.tracks.length - 1) {
      setCurrentTrackIndex(currentTrackIndex + 1);
    } else {
      setCurrentTrackIndex(null);
      setIsPlaying(false);
    }
  };

  return (
    <>
      <Album
        album={album}
        currentTrackIndex={currentTrackIndex}
        handlePlayTrack={handlePlayTrack}
        handlePause={handlePause}
        handleEnded={handleEnded}
      />

      {currentTrackIndex !== null && (
        <Player
          album={album}
          currentTrackIndex={currentTrackIndex}
          isPlaying={isPlaying}
          handlePlayTrack={handlePlayTrack}
          handlePause={handlePause}
          handleEnded={handleEnded}
        />
      )}
    </>
  );
};
