import React, { useState } from 'react';
import Track from './Track';
import ReactAudioPlayer from 'react-audio-player';

const Album = ({ album }) => {
  const { coverUrl, title, artist, tracks } = album;
  const [currentTrackIndex, setCurrentTrackIndex] = useState(null);

  const playAll = () => {
    setCurrentTrackIndex(0);
  };

  const playTrack = (index) => {
    setCurrentTrackIndex(index);
  };

  const handleTrackEnded = () => {
    if (currentTrackIndex < tracks.length - 1) {
      setCurrentTrackIndex(currentTrackIndex + 1);
    } else {
      setCurrentTrackIndex(null);
    }
  };

  return (
    <div className="album row">
      <h2>{title}</h2>
      <h4>{artist}</h4>
      <img src={coverUrl} alt={title} />
      <div className="playAlbum">
        <button onClick={playAll} className="playAll"></button>
      </div>

      <ul className="row">
        {tracks.map((track, index) => (
          <li key={index} className="row">
            <ReactAudioPlayer
              className="reactPlayer col-12"
              src={track.audioUrl}
              controls
              // key={index}
              // onPlay={() => playTrack(index)}
              // isPlaying={index === currentTrackIndex}
            />
          </li>
        ))}
      </ul>
      {currentTrackIndex !== null && (
        <audio
          src={tracks[currentTrackIndex].audioUrl}
          autoPlay
          onEnded={handleTrackEnded}
        />
      )}
    </div>
  );
};

export default Album;
