import React, { useState, useRef } from 'react';
import Track from './Track';
import ReactAudioPlayer from 'react-audio-player';

const Album = ({ album }) => {
  const { coverUrl, title, artist, tracks } = album;
  const [currentTrackIndex, setCurrentTrackIndex] = useState(null);
  const audioPlayerRef = useRef(null);

  const handlePlayTrack = (index) => {
    if (currentTrackIndex !== index) {
      // Pause any currently playing track
      const audioElement = audioPlayerRef.current.audioEl;
      if (audioElement) {
        audioElement.pause();
        audioElement.currentTime = 0;
      }
      setCurrentTrackIndex(index);
    } else {
      // Replay the current track from the beginning
      const audioElement = audioPlayerRef.current.audioEl;
      if (audioElement) {
        audioElement.currentTime = 0;
        audioElement.play();
      }
    }
  };

  return (
    <div className="album row">
      <h2>{title}</h2>
      <h4>{artist}</h4>
      <img src={coverUrl} alt={title} />

      <ReactAudioPlayer
        className="reactPlayer col-12"
        src={currentTrackIndex !== null ? tracks[currentTrackIndex].audioUrl : ''}
        controls
        ref={audioPlayerRef}
        autoPlay
      />

      <ul className="row">
        {tracks.map((track, index) => (
          <li key={index} className="row">
            <span onClick={() => handlePlayTrack(index)}>{track.title}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Album;
