import React, { useState, useEffect, useRef  } from 'react';
import Track from './Track';
import ReactAudioPlayer from 'react-audio-player';

const Album = ({ album }) => {
  const { coverUrl, title, artist, tracks } = album;
  const [currentTrackIndex, setCurrentTrackIndex] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioPlayerRef = useRef(null);

  useEffect(() => {
    // Play next track when current track ends
    const handleEnded = () => {
      if (currentTrackIndex < tracks.length - 1) {
        setCurrentTrackIndex(currentTrackIndex + 1);
      } else {
        setIsPlaying(false);
      }
    };

    if (audioPlayerRef.current) {
      audioPlayerRef.current.audioEl.addEventListener('ended', handleEnded);
    }

    return () => {
      if (audioPlayerRef.current) {
        audioPlayerRef.current.audioEl.removeEventListener('ended', handleEnded);
      }
    };
  }, [currentTrackIndex]);


  const handlePlayTrack = (index, event) => {
    event.preventDefault();
    if (currentTrackIndex !== null) {
      // Pause any currently playing track
      const audioElement = document.getElementById(`audio-${currentTrackIndex}`);
      audioElement.pause();
    }
    setCurrentTrackIndex(index);
    setIsPlaying(true);
  };

  const handlePause = () => {
    if (currentTrackIndex !== null) {
      // Pause any currently playing track
      const audioElement = document.getElementsByTagName('audio')[0];
      audioElement.pause();
    }
    setIsPlaying(false);
  };


  return (
    <div className="album row">
      <h2>{title}</h2>
      <h4>{artist}</h4>
      <img src={coverUrl} alt={title} />


      <ul className="row">
        {tracks.map((track, index) => (
          <li key={index} className="row">
            <ReactAudioPlayer 
              className="reactPlayer col-12"
              src={track.audioUrl}
              controls
              id={`audio-${index}`} // add this line to set the id of the audio element
              onPlay={() => handlePlayTrack(index)}
              onPause={() => setIsPlaying(false)}
              ref={(element) => {
                if (currentTrackIndex === index) {
                  audioPlayerRef.current = element;
                }
              }}
            />
          </li>
        ))}
      </ul>
      <button onClick={() => handlePlayTrack(0)}>Play All</button>
      <button onClick={handlePause}>Pause</button>
    </div>
  );
};

export default Album;
