import React, { useState, useRef } from 'react';
import Album from './Album';
import data from './data.json';
import MyNavbar from './MyNavbar';
import ReactAudioPlayer from 'react-audio-player';

const Albums = () => {
  const [currentTrackIndex, setCurrentTrackIndex] = useState(null);
  const audioPlayerRef = useRef(null);

  const handlePlayTrack = (index) => {
    setCurrentTrackIndex(index);
  };

  return (
    <div>
      <MyNavbar />
      <div className="albums">
        {data.map((album) => (
          <Album key={album.id} album={album} handlePlayTrack={handlePlayTrack} currentTrackIndex={currentTrackIndex} />
        ))}
      </div>

      <div className="playerArea">

        <div className="row">

          <div className="col-lg-1 thumbnailCol">
            {currentTrackIndex !== null && (
              <div className="thumbnail  mx-auto">
                <img src={data[currentTrackIndex].coverUrl} alt="Album cover" />
              </div>
            )}
          </div>

          <div className="col-lg-2 trackInfoCol">
            {currentTrackIndex !== null && (
              <div className="trackInfo">
                <div className="trackName">{data[currentTrackIndex].tracks[currentTrackIndex].title}</div>
                <div className="artistName">{data[currentTrackIndex].artist}</div>
              </div>
            )}
          </div>

          <div className="col-lg-6  reactPlayerCol mx-auto">
            <ReactAudioPlayer
              className="reactPlayer"
              src={currentTrackIndex !== null ? data[currentTrackIndex].tracks[currentTrackIndex].audioUrl : ''}
              controls
              ref={audioPlayerRef}
              autoPlay
            />
          </div>

        </div>



      </div>

    </div>


  );
};

export default Albums;
