import React, { useState, useEffect, useRef } from 'react';
import ReactAudioPlayer from 'react-audio-player';

const Album = ({ album }) => {
  const { coverUrl, title, artist, tracks } = album;
  const [currentTrackIndex, setCurrentTrackIndex] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioPlayerRef = useRef(null);

  useEffect(() => {
    // Play next track when current track ends
    const handleEnded = () => {
      if (currentTrackIndex < tracks.length - 1) {
        setCurrentTrackIndex(currentTrackIndex + 1);
      } else {
        setIsPlaying(false);
      }
    };

    const audioElement = audioPlayerRef.current.audioEl;
    audioElement.addEventListener('ended', handleEnded);
    return () => {
      audioElement.removeEventListener('ended', handleEnded);
    };
  }, [currentTrackIndex, tracks]);

  const handlePlayTrack = (index, event) => {
    event.preventDefault();
    if (currentTrackIndex !== null && index !== currentTrackIndex) {
      // Pause any currently playing track
      audioPlayerRef.current.audioEl.pause();
    }
    setCurrentTrackIndex(index);
    setIsPlaying(true);
  };

  const handlePause = () => {
    if (currentTrackIndex !== null) {
      // Pause any currently playing track
      audioPlayerRef.current.audioEl.pause();
    }
    setIsPlaying(false);
  };

  return (
    <div className="album row">
      <h2>{title}</h2>
      <h4>{artist}</h4>
      <img src={coverUrl} alt={title} />

      <ul className="row">
        {tracks.map((track, index) => (
          <li key={index} className="row">
            <button onClick={(event) => handlePlayTrack(index, event)}>Play</button>
            <span>{track.title}</span>
          </li>
        ))}
      </ul>

      <ReactAudioPlayer
        className="reactPlayer col-12"
        src={currentTrackIndex !== null ? tracks[currentTrackIndex].audioUrl : null}
        controls
        autoPlay={isPlaying}
        onPause={() => setIsPlaying(false)}
        ref={audioPlayerRef}
      />

      <button onClick={() => handlePlayTrack(0)}>Play All</button>
      <button onClick={handlePause}>Pause</button>
    </div>
  );
};

export default Album;
