import React, { useState } from 'react';
import Track from './Track';
import ReactAudioPlayer from 'react-audio-player';

const Album = ({ album }) => {
  const { coverUrl, title, artist, tracks } = album;
  const [currentTrackIndex, setCurrentTrackIndex] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioPlayerRef = useRef(null);

  useEffect(() => {
    // Play next track when current track ends
    const handleEnded = () => {
      if (currentTrackIndex < tracks.length - 1) {
        setCurrentTrackIndex(currentTrackIndex + 1);
      } else {
        setIsPlaying(false);
      }
    };

    if (audioPlayerRef.current) {
      audioPlayerRef.current.addEventListener('ended', handleEnded);
    }

    return () => {
      if (audioPlayerRef.current) {
        audioPlayerRef.current.removeEventListener('ended', handleEnded);
      }
    };
  }, [currentTrackIndex]);

  const handlePlayAll = () => {
    setCurrentTrackIndex(0);
    setIsPlaying(true);
  };

  const handlePlayTrack = (index) => {
    setCurrentTrackIndex(index);
    setIsPlaying(true);
  };

  const handlePause = () => {
    setIsPlaying(false);
  };



  return (
    <div className="album row">
      <h2>{title}</h2>
      <h4>{artist}</h4>
      <img src={coverUrl} alt={title} />


      <ul className="row">
        {tracks.map((track, index) => (
          <li key={index} className="row">
            <ReactAudioPlayer
              className="reactPlayer col-12"
              src={track.audioUrl}
              controls
            />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Album;
